import React, { useState } from "react";
import PropTypes from "prop-types";
import { Link } from "react-router-dom";
import ModalVideo from "react-modal-video";
import "react-modal-video/scss/modal-video.scss";
import "./styles.scss";
import TradingText from '../../../TradingText';

SliderItem.propTypes = {
  item: PropTypes.object,
};

function SliderItem(props) {
  const { item } = props;

  const [isOpen, setOpen] = useState(false);

  return (
    <div className={`box-slider ${item.classAction}`}>
      <img className="bg-slider" src={item.bgImg} alt="cybox" />
      <div className="box-slider__main">
        <div className="container">
          <div className="row">
            <div className="col-xl-6 col-md-12">
              <div className="content-box">
                {item.id === 1 ? ( // Check if the slide has id 1
                  <>
                    <TradingText /> {/* Use the TradingText component */}
                    <p className="sub-title">
                      Just got a whole lot easier
                    </p>
                  </>
                ) : (
                  <>
                    <h1 className="title">{item.title}</h1>
                    <p className="sub-title">{item.desc}</p>
                  </>
                )}
                <div className="wrap-btn">
                  <Link
                    to="#"
                    className="tf-button-st2 btn-effect"
                    data-toggle="modal"
                    data-target="#popup_bid"
                  >
                    <span className="effect">connect wallet</span>
                  </Link>
                  <button
                    to="#"
                    className="tf-button btn-effect popup-youtube"
                    onClick={() => setOpen(true)}
                  >
                    <span className="boder-fade"></span>
                    <span className="effect">watch video</span>
                  </button>
                </div>
              </div>
            </div>
            <div className="col-xl-6 col-md-12">
              {item.id !== 1 && ( // Check if the slide is not the first one
                <div className="image">
                  <img src={item.img} alt="cybox" />
                </div>
              )}
            </div>
          </div>
        </div>
      </div>

      <ModalVideo
        channel="youtube"
        autoplay
        isOpen={isOpen}
        videoId="i7EMACWuErA"
        onClose={() => setOpen(false)}
      />
    </div>
  );
}

export default SliderItem;
